mapper.select:
SELECT * FROM (SELECT Tb.*, rownum rNum FROM ( SELECT BOARD_SRL , USER_ID , LOCATION , SERVICE_TIME_START , SERVICE_TIME_END , TITLE , CONTENTS , POSTDATE , VISITCOUNT , CONTACT_TIME_START , CONTACT_TIME_END , LIKE_USER_ID , NVL2(length(like_user_id) - length(replace(like_user_id,',','')), length(like_user_id) - length(replace(like_user_id,',',''))+1,0) like_user_count, MAIN_IMAGE , CONTENTS_IMAGE , CATEGORY_SRL , SUBCATEGORY_SRL , service_expired , SERVICE_COST , LIMIT_PERSON , BOARD_TYPE , SERVICE_SCORE,LAST_UPDATE FROM op_board where board_srl > 0
and postdate > (sysdate-7)
AND board_type = #{board_type}
AND category_srl = #{category_srl}
AND subcategory_srl = #{subcategory_srl}
ORDER BY like_user_count desc, board_srl DESC ) Tb ) WHERE rNum Between #{pageStart} and #{pageEnd}

SELECT * FROM (SELECT Tb.*, rownum rNum FROM ( SELECT BOARD_SRL , USER_ID , LOCATION , SERVICE_TIME_START , SERVICE_TIME_END , TITLE , CONTENTS , POSTDATE , VISITCOUNT , CONTACT_TIME_START , CONTACT_TIME_END , LIKE_USER_ID , NVL2(length(like_user_id) - length(replace(like_user_id,',','')), length(like_user_id) - length(replace(like_user_id,',',''))+1,0) like_user_count, MAIN_IMAGE , CONTENTS_IMAGE , CATEGORY_SRL , SUBCATEGORY_SRL , service_expired , SERVICE_COST , LIMIT_PERSON , BOARD_TYPE , SERVICE_SCORE,LAST_UPDATE FROM op_board where board_srl > 0
and postdate > (sysdate-7)
AND board_type = #{board_type}
AND category_srl = #{category_srl}
AND subcategory_srl = #{subcategory_srl}
ORDER BY like_user_count desc, board_srl DESC ) Tb ) WHERE rNum Between #{pageStart} and #{pageEnd}

select sc.category_srl, category_name,subcategory_srl, subcategory_name from subcategory sc inner join category c on sc.category_srl = c.category_srl where subcategory_srl>0
and ( category_name like CONCAT(CONCAT('%', #{category_name}), '%')
OR subcategory_name like CONCAT(CONCAT('%', #{subcategory_name}), '%')
)
AND sc.category_srl = #{category_srl}
AND subcategory_srl = #{subcategory_srl}

SELECT * FROM (SELECT Tb.*, rownum rNum FROM ( SELECT BOARD_SRL , USER_ID , LOCATION , SERVICE_TIME_START , SERVICE_TIME_END , TITLE , CONTENTS , POSTDATE , VISITCOUNT , CONTACT_TIME_START , CONTACT_TIME_END , LIKE_USER_ID , NVL2(length(like_user_id) - length(replace(like_user_id,',','')), length(like_user_id) - length(replace(like_user_id,',',''))+1,0) like_user_count, MAIN_IMAGE , CONTENTS_IMAGE , CATEGORY_SRL , SUBCATEGORY_SRL , service_expired , SERVICE_COST , LIMIT_PERSON , BOARD_TYPE , SERVICE_SCORE,LAST_UPDATE FROM op_board where board_srl > 0
AND board_type = #{board_type}
AND category_srl = #{category_srl}
AND subcategory_srl = #{subcategory_srl}
AND user_id = #{user_id,jdbcType=VARCHAR}
AND service_expired = #{service_expired,jdbcType=VARCHAR}
ORDER BY board_srl DESC ) Tb ) WHERE rNum Between #{pageStart} and #{pageEnd}



select count(*) from op_board
  where board_srl > 0 







		AND board_type = #{board_type}








